import logo from "./logo.svg";
import "./App.css";
import { Board } from "./game/Board";
import { Piece } from "./game/Piece";

function App() {
  const board = new Board(3);
  board.board[0][2].placePiece(Piece.Blue)
  board.board[2][2].placePiece(Piece.Red)
  return (
    <div className="App">
      {board.board.map((row) => (
        <div>
          {row.map((square) => (
            <SquareComponent piece={square} />
          ))}
        </div>
      ))}
    </div>
  );
}

function SquareComponent({ square }) {
  let piece = square.piece
  return (
    <>
      <span 
      onClick={ square.placePiece(Piece.Blue) }
        className={`square ${
          piece == Piece.Blue ? "blue" : piece == Piece.Red ? "red" : "empty"
        }`}
      ></span>
    </>
  );
}

export default App;
