import logo from "./logo.svg";
import "./App.css";
import { Board } from "./game/Board";
import { Piece } from "./game/Piece";

function App() {
  const board = new Board(3);
  console.log(board);
  console.log(board.board);
  // board.board[0][0].placePiece(Piece.Blue)
  // board.board[1][0].placePiece(Piece.Red)
  return (
    <div className="App">
      {board.board.map((row) => (
        <div>
          {row.map((square) => (
            <SquareComponent piece={square.piece} />
          ))}
        </div>
      ))}
    </div>
  );
}

function SquareComponent({ piece }) {
  return (
    <>
      <span
        className={`square ${
          piece == Piece.Blue ? "blue" : piece == Piece.Red ? "red" : "empty"
        }`}
      ></span>
    </>
  );
}

export default App;
