      }

  return (
    <div className="App">
      {board.board.map((row) => (
        <div>
          {row.map((square) => (
            <SquareComponent square={square} />
          ))}
        </div>
      ))}
    </div>
  );
}

function SquareComponent({ square }) {
  let piece = square.piece
  return (
    <>
      <span
      onClick={clickHandler}
        className={`square ${
          piece == Piece.Blue ? "blue" : piece == Piece.Red ? "red" : "empty"
        }`}
      ></span>
    </>
  );
}

export default App;
